const burger=document.querySelector(".burger"),menuNav=document.querySelector(".navigation");function show(){burger.classList.contains("show")?(burger.classList.remove("show"),menuNav.classList.remove("showNav")):(burger.classList.add("show"),menuNav.classList.add("showNav"))}burger.addEventListener("click",show),document.querySelector(".header__search").addEventListener("click",()=>{document.querySelector(".header__stat").classList.toggle("search__active")});